import '../styles/App.css';
import {useState} from 'react';


function App() {

const [ingredientsList, setIngredients] = useState(false);

const handleIngredients= (ev)=> {
  setIngredients(ev.target.checked);
}



  return (
    <div >
      <h1>Selecciona los ingredientes de la tortilla de patatas</h1>
      <form>
        <label htmlFor="ingredients">Macarrones</label>
        <input onChange={handleIngredients} type="checkbox" name='ingredients' id='macarrones'/>
        <label htmlFor="ingredientes">Patatas</label>
        <input onChange={handleIngredients} type="checkbox" name='patatas' id='patatas'/>
        <label htmlFor="ingredientes">Nueces</label>
        <input onChange={handleIngredients} type="checkbox" name='nueces' id='nueces'/>
        <label htmlFor="ingredientes">Huevos</label>
        <input onChange={handleIngredients} type="checkbox" name='huevos' id='huevos'/>
        <label htmlFor="ingredientes">Cebolla</label>
        <input onChange={handleIngredients} type="checkbox" name='cebolla' id='cebolla'/>
        <label htmlFor="ingredientes">Cerveza</label>
        <input onChange={handleIngredients} type="checkbox" name='cerveza' id='cerveza'/>
      </form>
    </div>
  );
}

export default App;
