//NOTAS: los inputs siempre tienen que estar contralados por onChange y value


import '../styles/App.scss';
import { useState} from 'react';
import originalData from '../data/tasks.json'

function App() {

  const[filterTask, setFilterTask]= useState(''); //esta variable de estado se crea para el input de filtrado
  const [newTask, setNewTask] = useState(''); // esta variable de estado se crea para ir a単adiendo las nuevas tareas del input
  const [data, setData]= useState(originalData); //esta variable es para nuestra lista de tareas




  // renderizar titulo y lista de tareas-----------------------------

  //RECORDATORIO: el ternario lo dejo con === true para verlo bien como ejemplo pero es redundante, no hay que ponerlo
const renderTitle = (text) => <h1>{text}</h1>

const renderTasks = () => {
  console.log(data);
  return data.map((eachTask, id)=>{
    return (
      <li key={id} className={eachTask.completed===true?'paint':''}> 
        {eachTask.task}
      </li>
    );
  });
}
//-------------------------------------------------------------------
//A単adir tareas nuevas a la lista: crear variable de estado, y crear nuevo objeto con el spread operator

const handleChangeNew =(ev)=> {
  setNewTask (ev.currentTarget.value);
}
const handleClickSave =()=> {
  const newObjet = {
    id: data.length+1,
    task: newTask,
    completed: false,
  };


  setData ([...data, newObjet]);
  setNewTask('');
};


//-------------------------------------------------------------------
//Filtrar por letras en un input 

const handleChangeFilter = (ev)=>{
  setFilterTask (ev.currentTarget)
}

//-------------------------------------------------------------------


  return (
    <div>
      <div className='title'>
        <form action="" onSubmit={(ev) =>ev.preventDefault()}>
          <label htmlFor="">Filtrar por nombre</label>
          <input type="text"name='filterTask' id='filterTask' onChange={handleChangeFilter} value={filterTask} />
        </form>
        {renderTitle('Mi lista de tareas')}
      </div>
      <ol>{renderTasks()}</ol>
      <form action="" onSubmit={(ev) => ev.preventDefault()}>
        <input type="text" 
        name='newTask'
        id='newTask' 
        onChange={handleChangeNew} 
        value={newTask} />
        <button onClick={handleClickSave}>Guardar</button>
      </form>
    </div>
  );
}

export default App;
