import '../styles/App.scss';
import { useState} from 'react';

function App() {

  const [newTask, setNewTask] = useState('')


  const tasks = [
    { task: 'Comprar harina, jamón y pan rallado', completed: true },
    { task: 'Hacer croquetas ricas', completed: true },
    { task: 'Ir a la puerta de un gimnasio', completed: false },
    { task: 'Comerme las croquetas mirando a la gente que entra en el gimnasio', completed: false }
  ];
  // renderizar titulo y lista de tareas-----------------------------

  //RECORDATORIO: el ternario lo dejo con === true para verlo bien como ejemplo pero es redundante, no hay que ponerlo
const renderTitle = (text) => <h1>{text}</h1>

const renderTasks = () => {
  return tasks.map((eachTask, index)=>{
    return (
      <li key={index} className={eachTask.completed===true?'paint':''}> 
        {eachTask.task}
      </li>
    );
  });
}
//-------------------------------------------------------------------


  return (
    <div>
      <div className='title'>
        {renderTitle('Mi lista de tareas')}
      </div>
      <ol>{renderTasks()}</ol>
    </div>
  );
}

export default App;
