import '../styles/App.scss';

function App() {
  const tasks = [
    { task: 'Comprar harina, jamón y pan rallado', completed: true },
    { task: 'Hacer croquetas ricas', completed: true },
    { task: 'Ir a la puerta de un gimnasio', completed: false },
    {
      task: 'Comerme las croquetas mirando a la gente que entra en el gimnasio',
      completed: false,
    },
  ];
  // renderizar titulo y lista de tareas-----------------------------
  const renderTitle = (text) => <h1>{text}</h1>;
  const renderTasks = () => {
    return tasks.map((allTask, index) => {
      if (task.completed === true) {
          return (
        <li key={index} className="paint-li">
          <span>{allTask.task}</span>
        </li>
      );
      }

    });
  };
  //-------------------------------------------------------------------

  return (
    <div>
      <div className="title">{renderTitle('Mi lista de tareas')}</div>
      <ol>{renderTasks()}</ol>
    </div>
  );
}

export default App;
