import '../styles/App.scss';
import { useState} from 'react';

function App() {

  const [newTask, setNewTask] = useState('');
  const [data, setData]= useState(tasks);


  const tasks = [
    { id:1, task: 'Comprar harina, jam칩n y pan rallado', completed: true },
    { id:2, task: 'Hacer croquetas ricas', completed: true },
    { id:3, task: 'Ir a la puerta de un gimnasio', completed: false },
    { id:4, task: 'Comerme las croquetas mirando a la gente que entra en el gimnasio', completed: false }
  ];

  // renderizar titulo y lista de tareas-----------------------------

  //RECORDATORIO: el ternario lo dejo con === true para verlo bien como ejemplo pero es redundante, no hay que ponerlo
const renderTitle = (text) => <h1>{text}</h1>

const renderTasks = () => {
  return data.map((eachTask, id)=>{
    return (
      <li key={index} className={eachTask.completed===true?'paint':''}> 
        {eachTask.task}
      </li>
    );
  });
}
//-------------------------------------------------------------------
//A침adir tareas nuevas a la lista con eventos: crear variable de estado, 

const handleChangeNew =(ev)=> {
  setNewTask (ev.currentTarget.value);
}
const handleClickSave =()=> {
  const newObjet = {
    id: data.length+1,
    task: newTask,
    completed: false,
  };


  setData(...data, newObjet);
  setNewTask('');
};


//-------------------------------------------------------------------


  return (
    <div>
      <div className='title'>
        {renderTitle('Mi lista de tareas')}
      </div>
      <ol>{renderTasks()}</ol>
      <form action="" onSubmit={(ev) => ev.preventDefault()}>
        <input type="text" 
        name='newTask'
        id='newTask' 
        onChange={handleChangeNew} 
        value={newTask} />
        <button onClick={handleClickSave}>Guardar</button>
      </form>
    </div>
  );
}

export default App;
