import '../styles/App.css';

function App() {
  const tasks = [
    'Comprar harina, jamón y pan rallado',
    'Hacer croquetas ricas',
    'Ir a la puerta de un gimnasio',
    'Comerme las croquetas mirando a la gente que entra en el gimnasio'
  ];
  //renderizar titulo y lista de tareas-----------------------------
const renderTitle = (text) => <h1>{text}</h1>
const renderTasks = () => {
  return tasks.map((task, index)=>{
    return (
      <li key={index}>
        <span>{task}</span>
      </li>
    );
  });
}
//------------------------------------------------------------------


  return (
    <div>
      <div className='title'>
        {renderTitle('Mi lista de tareas')}
      </div>
      <ol>{renderTasks()}</ol>
    </div>
  );
}

export default App;
