const InputGroupText = (props) => {

  const handleName = (ev) => {
    const handle = ev.currentTarget.name;
    const inputValue = ev.currentTarget.value;
    props.handleName(inputChange, inputValue);
  };

    return (
        <div className="input-group-text">
  <label className="label-text" htmlFor="name">
    {props.labeltext}Escribe un nombre
  </label>
  <input
    className="input-text"
    type="text"
    name={props.name}
    id={props.id}
    placeholder={props.placeholder}
    value={props.name}
    onChange={handleName}
  />
</div> 

    )
};

export default InputGroupText;
